require_once dirname(__FILE__) . '/base_model.php';

class imslots_game_logs extends BaseModel {

	function __construct() {
		parent::__construct();
	}

	protected $tableName = "imslots_game_logs";

	public function insertImslotsGameLogs($data) {
		 $this->db->insert($this->tableName, $data);
	}

	public function updateImslotsGameLogs($data) {
		$this->db->where('external_uniqueid', $data['external_uniqueid']);
		return $this->db->update($this->tableName, $data);
	}

	public function syncToImptGameLogs($data) {
		return $this->db->insert($this->tableName, $data);
	}

	public function isUniqueIdAlreadyExists($uniqueId) {
		$qry = $this->db->get_where($this->tableName, array('GameCode' => $uniqueId));
		if ($this->getOneRow($qry) == null) {
			return false;
		} else {
			return true;
		}
	}

	public function getImslotsGameLogStatistics($dateFrom, $dateTo) {

		$sql = <<<EOD
		SELECT
			  `imslots_game_logs`.`PlayerId`,
			  `imslots_game_logs`.`Username`,
			  `imslots_game_logs`.`external_uniqueid`,
			  `imslots_game_logs`.`StartTime` AS date_created,
			  `imslots_game_logs`.`IMGameCode` AS game_code,
			  `imslots_game_logs`.`WinAmount` AS result_amount,
			  `imslots_game_logs`.`BetAmount` AS bet_amount,
			  `imslots_game_logs`.`response_result_id`,
			  `game_description`.`id`  AS game_description_id,
			  `game_description`.`game_name` AS game,
			  `game_description`.`game_code`,
			  `game_description`.`game_type_id`,
			  `game_description`.`void_bet`,
			  `game_type`.`game_type`
		FROM
			  `imslots_game_logs`
			  LEFT JOIN `game_description`
			    ON (
			      `imslots_game_logs`.`IMGameCode` COLLATE utf8_unicode_ci = `game_description`.`game_code`
			      AND `game_description`.`game_platform_id` = ?
			      AND `game_description`.`void_bet` != 1
			    )
			  LEFT JOIN `game_type`
			    ON (
			      `game_description`.`game_type_id` = `game_type`.`id`
			    )
			  LEFT JOIN `game_provider_auth`
			    ON (
			      `imslots_game_logs`.`Username` COLLATE utf8_unicode_ci = `game_provider_auth`.`login_name`
			      AND `game_provider_auth`.`game_provider_id` = ?
			    )
		WHERE (
			    `imslots_game_logs`.`StartTime` >= ?
			    AND `imslots_game_logs`.`EndTime` <= ?
			    )
EOD;

		$query = $this->db->query($sql, array(
			IMSLOTS_API,
			IMSLOTS_API,
			$dateFrom,
			$dateTo,
		));

		return $this->getMultipleRow($query);
	}

	public function getAvailableRows($rows) {
		//echo "<pre>";print_r($rows);exit;
		$this->db->select('RoundId')->from($this->tableName)->where_in('RoundId', array_column($rows,6));
		$existsRow = $this->runMultipleRowArray();
		$availableRows = null;
		if (!empty($existsRow)) {
			$existsId = array_column($existsRow, 'RoundId');
			$availableRows = array();
			foreach ($rows as $row) {
				$uniqueId = $row[6];
				//echo $uniqueId."<pre>";print_r($existsId);exit;
				if (!in_array($uniqueId, $existsId)) {
					$availableRows[] = $row;
				}
			}
		} else {
			$availableRows = $rows;
		}
		//echo "<pre>";print_r($availableRows);exit;

		return $availableRows;
	}

	public function syncGameList($gamelist){
		$gamelist = json_decode($gamelist,true);
		//echo "<pre>";print_r($gamelist);exit;
		$this->db->trans_start();
		 $game_description_list = array();
        foreach ($gamelist as $game_type) {
			$this->db->select('*')->from('game_type')->where(array('game_type_lang'=>$game_type['game_type_lang'],'game_platform_id'=>IMSLOTS_API));
			$query = $this->db->get();
			$return_type = json_decode(json_encode($query->result()),true);
			if(!count($return_type)){
				$this->db->insert('game_type', array(
	                'game_platform_id' => IMSLOTS_API,
	                'game_type' => $game_type['game_type'],
	                'game_type_lang' => $game_type['game_type_lang'],
	                'status' => $game_type['status'],
	                'flag_show_in_site' => $game_type['flag_show_in_site'],
	            ));
	            $game_type_id = $this->db->insert_id();
			}else{
				$game_type_id = $return_type[0]['id'];
			}

            foreach ($game_type['game_description_list'] as $game_description) {
            	$this->db->select('*')->from('game_description')->where(array('game_name'=>$game_description['game_name'],'game_platform_id'=>IMSLOTS_API));
				$query = $this->db->get();
				$return_gamedesc = json_decode(json_encode($query->result()),true);

            	if(!count($return_gamedesc)){
            		$game_description_list[] = array_merge(array(
	                    'game_platform_id' => IMSLOTS_API,
	                    'game_type_id' => $game_type_id
	                ), $game_description);
            	}
            }
        }
        if(count($game_description_list)){
        	$this->db->insert_batch('game_description', $game_description_list);
        }
        $this->db->trans_commit();
	}

