        position: relative;
        top: 2px;
    }

    #shop_points .tab-content {
        padding: 0;
    }

    #shop_points .tab-content .tab-pane {
        padding: 25px 0;
    }

    #shop_points .tab-content .tab-pane input,
    #shop_points .tab-content .tab-pane select {
