    }

    #shop_points .tab-content .tab-pane .time_input {
        margin-top: 15px;
    }

    #shop_points .tab-content .tab-pane .time_input .calendar::before {
        content: '';
        background-color: #fff;
        background-image: url('data:image/png;base64,');
        background-repeat: no-repeat;
        background-size: cover;
        display: block;
        width: 20px;
        height: 20px;
        position: absolute;
