        background-position-y: 5px;
    }

    #shop_points .tab-content .tab-pane .time_input .time option {
        text-align: center;
        border-bottom: 1px solid rgba(0, 0, 0, .1);
    }

    .time_input input[type="date"]::-webkit-calendar-picker-indicator {
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        width: auto;
        height: auto;
        color: transparent;
        background: transparent;
    }

    #shop_points .tab-content .tab-pane .time_input small {
        padding: 0 15px;
        font-weight: bold;
    }

    #shop_points .tab-content .tab-pane input:focus,
