</script>
<script>
    $(document).ready(function () {
        $('input[name="intervaltype"]').click(function () {
            $(this).tab('show');
            $(this).removeClass('active');
        });

        $.getJSON( "/marketing_management/getShoppingPointExpirationSetting", function( expirationSetting ) {
            console.log(expirationSetting);
            $.each(expirationSetting, function( item, setting ) {
                if (item == 'intervaltype'){
                    $('#opt_'+setting).prop('checked',true).trigger('click');
                } else {
                    $('[name="'+ item +'"]').val(setting);
                }
            });
         });
    })
</script>