// JavaScript Document

$(document).ready(function(){
            var queueOpen = [],queueClose = [];			
			$('.menu_rim li').hover(function(){
                    var index = $(this).index();
                    if(queueOpen[index] || queueClose[index]){
                        return false;
                    }
                    queueOpen[index] = true;
                    var dl = $(this).children('dl');
                    var height = dl.height();
                    $this = $(this);
                    dl.height(0).show().animate({height:height},250,function(){
                        queueOpen[index] = false;
                    })
                },function(){
                    var index = $(this).index();
                    if(queueClose[index]){
                        return false;
                    }
                    queueClose[index] = true;
                    var dl = $(this).children('dl');
                    $this = $(this);
                    dl.animate({height:0},250,function(){
                        dl.hide();
                        dl.attr("style","");
                        queueClose[index] = false;
                    })
                });
				
				
				$(".menu_rim li").hover(function(){
				$(this).children('a').addClass('current');
				},function(){
				$(this).children('a').removeClass('current');
				});

				 })
				 
		
				 




